package laporan4;

/**
 * @author asrul
 *
 */
public class ForDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 10, j = 3; i >= -3 & j < 10; i--, j++) {
			System.out.println("For 1 : " + i + " " + j);
		}
		for (int i = 1; i < 2; i++) {
			System.out.println("For 2 : " + i);
		}
		for (int i = 5; i >= -5; i--) {
			System.out.println("For 3 : " + i);
		}
	}

}
