package laporan4;

/**
 * @author asrul
 *
 */
public class SwitchTest {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int code = 1;
		switch (code) {
		case 1:
			System.out.println("Xperia");
			break;
		case 2:
			System.out.println("Samsung");
			break;
		case 3:
			System.out.println("Lenovo");
			break;
		case 4:
			System.out.println("Motorola");
			break;
		case 5:
			System.out.println("HTC");
			break;
		case 6:
			System.out.println("LG");
			break;
		case 7:
			System.out.println("Nokia");
			break;
		case 8:
			System.out.println("Xiaomi");
			break;
		case 9:
			System.out.println("Advan");
			break;
		case 10:
			System.out.println("Oppo");
			break;
		case 11:
			System.out.println("Evercoss");
			break;
		case 12:
			System.out.println("Mito");
			break;
		default:
			System.out.println("Kode Merek Handphone Salah");
			break;
		}
	}
}
